code_section.example:
export async function execute(input, context) {
  // Validate input
  if (!input.message) {
    throw new Error('message is required');
  }

  // Use RCRT API
  await context.api.createBreadcrumb({
    schema_name: 'my.output.v1',
    title: 'Tool Output',
    tags: ['tool:output'],
    context: { result: input.message }
  });

  // Return output matching output_schema
  return { echo: input.message };
}

right_way.example_code:
// Get config (contains secret IDs, not values)
let config = {};
if (input.config_id) {
  const configBreadcrumb = await context.api.getBreadcrumb(input.config_id);
  if (configBreadcrumb.schema_name === 'tool.config.v1') {
    config = configBreadcrumb.context.config || {};
  }
}

// Load secret by ID
if (config.apiKeySecretId) {
  const secretBreadcrumb = await context.api.getSecret(
    config.apiKeySecretId,
    'API authentication'
  );
  const apiKey = secretBreadcrumb.value;
} else {
  throw new Error('No API key configured. Please set apiKeySecretId in tool config.');
}

code.source:
export async function execute(input, context) {
  if (!input.message || typeof input.message !== 'string') {
    throw new Error('message is required and must be a string');
  }
  return { echo: input.message };
}

calling_other_tools.example:
await context.api.createBreadcrumb({
  schema_name: 'tool.request.v1',
  title: 'Call Calculator',
  tags: ['tool:request', 'workspace:tools'],
  context: {
    tool: 'calculator',
    input: { expression: '2 + 2' },
    requestId: `sub-request-${Date.now()}`
  }
});
