/**
 * Breadcrumb Create Tool - Self-Contained Deno Version
 * Creates new breadcrumbs in the RCRT system
 */

interface Input {
  schema_name: string;
  title: string;
  tags?: string[];
  context?: any;
  visibility?: 'public' | 'private';
  sensitivity?: 'public' | 'internal' | 'confidential';
}

interface Output {
  success: boolean;
  id?: string;
  breadcrumb?: any;
  error?: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  // Validate required fields
  if (!input.schema_name) {
    throw new Error('schema_name is required');
  }

  if (!input.title) {
    throw new Error('title is required');
  }

  try {
    // Create breadcrumb
    const result = await context.api.createBreadcrumb({
      schema_name: input.schema_name,
      title: input.title,
      tags: input.tags || [],
      context: input.context || {},
      visibility: input.visibility,
      sensitivity: input.sensitivity
    });

    return {
      success: true,
      id: result.id,
      breadcrumb: result
    };
  } catch (error: any) {
    return {
      success: false,
      error: error.message || 'Failed to create breadcrumb'
    };
  }
}
