/**
 * Breadcrumb Search Tool - Self-Contained Deno Version
 * Search breadcrumbs using various methods
 */

interface Input {
  query?: string;          // Semantic search query
  schema_name?: string;    // Filter by schema
  tag?: string;            // Filter by tag
  tags?: string[];         // Filter by multiple tags
  limit?: number;          // Max results
  nn?: number;             // Number of nearest neighbors for vector search
}

interface Output {
  breadcrumbs: any[];
  count: number;
  method: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  const limit = input.limit || 10;
  let breadcrumbs: any[];
  let method: string;

  // Vector search if query provided
  if (input.query) {
    method = 'vector';
    breadcrumbs = await context.api.vectorSearch({
      q: input.query,
      nn: input.nn || 5,
      schema_name: input.schema_name,
      tag: input.tag
    });
  }
  // Tag/schema search
  else {
    method = 'filter';
    const searchParams: any = {};

    if (input.schema_name) searchParams.schema_name = input.schema_name;
    if (input.tag) searchParams.tag = input.tag;
    if (input.tags) searchParams.tags = input.tags;

    breadcrumbs = await context.api.searchBreadcrumbs(searchParams);
  }

  // Limit results
  const limited = breadcrumbs.slice(0, limit);

  return {
    breadcrumbs: limited,
    count: limited.length,
    method
  };
}
