/**
 * Breadcrumb Update Tool - Self-Contained Deno Version
 * Updates existing breadcrumbs in the RCRT system
 */

interface Input {
  breadcrumb_id: string;
  version?: number;
  updates: {
    context?: any;
    tags?: string[];
    title?: string;
  };
}

interface Output {
  success: boolean;
  id?: string;
  new_version?: number;
  breadcrumb?: any;
  error?: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  // Validate required fields
  if (!input.breadcrumb_id) {
    throw new Error('breadcrumb_id is required');
  }

  if (!input.updates) {
    throw new Error('updates object is required');
  }

  if (Object.keys(input.updates).length === 0) {
    throw new Error('updates must contain at least one field to update');
  }

  try {
    // Get current breadcrumb to get version if not provided
    let version = input.version;
    if (!version) {
      const current = await context.api.getBreadcrumb(input.breadcrumb_id);
      version = current.version;
    }

    // Update breadcrumb
    await context.api.updateBreadcrumb(
      input.breadcrumb_id,
      version,
      input.updates
    );

    // Fetch updated breadcrumb to get new version
    const updated = await context.api.getBreadcrumb(input.breadcrumb_id);

    return {
      success: true,
      id: input.breadcrumb_id,
      new_version: updated.version,
      breadcrumb: updated
    };
  } catch (error: any) {
    return {
      success: false,
      error: error.message || 'Failed to update breadcrumb'
    };
  }
}
