/**
 * Calculator Tool - Self-Contained Deno Version
 * Performs mathematical calculations safely
 */

interface Input {
  expression: string;
}

interface Output {
  result: number;
  expression: string;
  formatted: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  const { expression } = input;

  if (!expression) {
    throw new Error('Missing required field: expression');
  }

  try {
    // Create a safer eval context with Math functions
    const mathContext = {
      // Basic Math constants
      PI: Math.PI,
      E: Math.E,
      // Basic Math functions
      abs: Math.abs,
      acos: Math.acos,
      asin: Math.asin,
      atan: Math.atan,
      atan2: Math.atan2,
      ceil: Math.ceil,
      cos: Math.cos,
      exp: Math.exp,
      floor: Math.floor,
      log: Math.log,
      max: Math.max,
      min: Math.min,
      pow: Math.pow,
      random: Math.random,
      round: Math.round,
      sin: Math.sin,
      sqrt: Math.sqrt,
      tan: Math.tan
    };

    // Build function with math context
    const mathKeys = Object.keys(mathContext);
    const mathValues = Object.values(mathContext);

    // Sanitize expression - remove potentially dangerous patterns
    const sanitized = expression
      .replace(/[^0-9+\-*/().,\s\w]/g, '') // Only allow basic math chars
      .replace(/\bMath\./g, ''); // Remove Math. prefix for cleaner syntax

    // Create function with math context
    const evalFunction = new Function(...mathKeys, `"use strict"; return (${sanitized})`);
    const result = evalFunction(...mathValues);

    // Validate result
    if (typeof result !== 'number' || !isFinite(result)) {
      throw new Error('Expression did not evaluate to a valid number');
    }

    // Format result nicely
    const formatted = result % 1 === 0 
      ? result.toString() 
      : result.toFixed(10).replace(/\.?0+$/, ''); // Remove trailing zeros

    return {
      result,
      expression,
      formatted: `${expression} = ${formatted}`
    };
  } catch (error: any) {
    throw new Error(`Failed to evaluate expression: ${error.message}`);
  }
}
