/**
 * JSON Transform Tool - Self-Contained Deno Version
 * Transform JSON using simple path-based mappings
 */

interface Input {
  data: any;
  operation: 'extract' | 'map' | 'filter' | 'sort';
  path?: string;           // For extract: $.field.nested
  mapping?: Record<string, string>;  // For map: {newField: '$.oldField'}
  condition?: any;         // For filter
  sort_by?: string;        // For sort
}

interface Output {
  result: any;
  operation: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

function getByPath(obj: any, path: string): any {
  const cleanPath = path.replace(/^\$\./, '');
  const parts = cleanPath.split('.');
  let current = obj;

  for (const part of parts) {
    if (current === undefined || current === null) return undefined;
    current = current[part];
  }

  return current;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  if (!input.data) {
    throw new Error('data is required');
  }

  if (!input.operation) {
    throw new Error('operation is required');
  }

  let result: any;

  switch (input.operation) {
    case 'extract':
      if (!input.path) {
        throw new Error('path is required for extract operation');
      }
      result = getByPath(input.data, input.path);
      break;

    case 'map':
      if (!input.mapping) {
        throw new Error('mapping is required for map operation');
      }
      result = {};
      for (const [newKey, oldPath] of Object.entries(input.mapping)) {
        result[newKey] = getByPath(input.data, oldPath);
      }
      break;

    case 'filter':
      if (!Array.isArray(input.data)) {
        throw new Error('data must be an array for filter operation');
      }
      if (!input.condition) {
        throw new Error('condition is required for filter operation');
      }
      // Simple filtering by exact match
      result = input.data.filter((item: any) => {
        for (const [key, value] of Object.entries(input.condition)) {
          if (item[key] !== value) return false;
        }
        return true;
      });
      break;

    case 'sort':
      if (!Array.isArray(input.data)) {
        throw new Error('data must be an array for sort operation');
      }
      if (!input.sort_by) {
        throw new Error('sort_by is required for sort operation');
      }
      result = [...input.data].sort((a, b) => {
        const aVal = getByPath(a, input.sort_by!);
        const bVal = getByPath(b, input.sort_by!);
        if (aVal < bVal) return -1;
        if (aVal > bVal) return 1;
        return 0;
      });
      break;

    default:
      throw new Error(`Unknown operation: ${input.operation}`);
  }

  return {
    result,
    operation: input.operation
  };
}
