/**
 * OpenRouter Models Sync Tool - Self-Contained Deno Version
 * Fetches latest models from OpenRouter public API and publishes as breadcrumb
 */

interface Input {
  force_refresh?: boolean;
}

interface Output {
  models_count: number;
  catalog_id: string;
  last_updated: string;
  message: string;
}

interface Context {
  api: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  // Detect invocation source
  const triggerSchema = context.request?.trigger_event?.schema_name;
  const isBootstrap = triggerSchema === 'system.startup.v1';
  const isScheduled = triggerSchema === 'schedule.tick.v1';
  const isManual = triggerSchema === 'tool.request.v1' && !context.request?.trigger_event?.tags?.includes('auto-trigger');

  let invocationSource = 'unknown';
  if (isBootstrap) invocationSource = 'bootstrap';
  else if (isScheduled) invocationSource = 'scheduled';
  else if (isManual) invocationSource = 'manual/UI';

  // Invoked from: ${invocationSource}

  // Check if we need to refresh
  if (!input.force_refresh) {
    try {
      const existing = await context.api.searchBreadcrumbs({
        schema_name: 'openrouter.models.catalog.v1',
        tag: 'openrouter:models',
        limit: 1
      });

      if (existing.length > 0) {
        const catalog = existing[0];
        const lastUpdated = new Date(catalog.context.last_updated || catalog.updated_at);
        const hoursSinceUpdate = (Date.now() - lastUpdated.getTime()) / (1000 * 60 * 60);

        // Only refresh if older than 24 hours
        if (hoursSinceUpdate < 24) {
          console.log(`âœ… Catalog is fresh (${hoursSinceUpdate.toFixed(1)} hours old)`);
          return {
            models_count: catalog.context.models?.length || 0,
            catalog_id: catalog.id,
            last_updated: catalog.context.last_updated || catalog.updated_at,
            message: `Catalog is up-to-date (${hoursSinceUpdate.toFixed(1)} hours old). Use force_refresh: true to update anyway.`
          };
        }
      }
    } catch (error) {
      console.warn('Failed to check existing catalog:', error);
    }
  }

  // Fetch models from OpenRouter public API
  console.log('ðŸ“¥ Fetching models from OpenRouter...');
  const response = await fetch('https://openrouter.ai/api/v1/models');

  if (!response.ok) {
    throw new Error(`Failed to fetch models: ${response.status} ${response.statusText}`);
  }

  const data = await response.json();

  if (!data.data || !Array.isArray(data.data)) {
    throw new Error('Invalid response format from OpenRouter API');
  }

  const models = data.data;
  console.log(`âœ… Fetched ${models.length} models`);

  // Check if catalog breadcrumb exists
  const existingCatalogs = await context.api.searchBreadcrumbs({
    schema_name: 'openrouter.models.catalog.v1',
    tag: 'openrouter:models'
  });

  const now = new Date().toISOString();
  const catalogData = {
    title: 'OpenRouter Models Catalog',
    schema_name: 'openrouter.models.catalog.v1',
    tags: ['openrouter:models', 'catalog', 'sync', 'system'],
    context: {
      models: models,
      last_updated: now,
      source: 'https://openrouter.ai/api/v1/models',
      total_count: models.length,
      sync_tool: 'openrouter_models_sync'
    }
  };

  let catalogId: string;

  if (existingCatalogs.length > 0) {
    // Update existing catalog
    const existing = existingCatalogs[0];
    catalogId = existing.id;

    console.log(`ðŸ”„ Updating existing catalog: ${catalogId}`);
    await context.api.updateBreadcrumb(catalogId, catalogData);
  } else {
    // Create new catalog
    console.log('âž• Creating new models catalog breadcrumb');
    const created = await context.api.createBreadcrumb(catalogData);
    catalogId = created.id;
  }

  console.log(`âœ… Models catalog synced successfully!`);

  return {
    models_count: models.length,
    catalog_id: catalogId,
    last_updated: now,
    message: `Successfully synced ${models.length} models from OpenRouter`
  };
}
