/**
 * OpenRouter LLM Tool - Self-Contained Deno Version
 * Accesses 100+ models with real pricing
 * RCRT-compliant: Loads secrets by ID from config
 */

interface Message {
  role: 'system' | 'user' | 'assistant';
  content: string;
}

interface Input {
  messages: Message[];
  model?: string;
  temperature?: number;
  max_tokens?: number;
  config_id?: string;
}

interface Output {
  content: string;
  model: string;
  usage: {
    prompt_tokens: number;
    completion_tokens: number;
    total_tokens: number;
  };
  cost_estimate: number;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  // Validate input
  if (!input.messages || !Array.isArray(input.messages) || input.messages.length === 0) {
    throw new Error('messages array is required and must not be empty');
  }

  // Get config from breadcrumb if config_id provided
  let config: any = {};
  if (input.config_id || context.request.trigger_event?.context?.config_id) {
    const configId = input.config_id || context.request.trigger_event?.context?.config_id;
    try {
      const configBreadcrumb = await context.api.getBreadcrumb(configId);
      if (configBreadcrumb.schema_name === 'tool.config.v1') {
        config = configBreadcrumb.context.config || {};
      }
    } catch (error) {
      console.warn(`Failed to load config from ${configId}:`, error);
    }
  }

  // Get API key from config (RCRT way: reference by breadcrumb ID)
  let apiKey: string | undefined;

  if (config.apiKeySecretId) {
    // Load secret by ID from config
    try {
      const secretBreadcrumb = await context.api.getSecret(config.apiKeySecretId, 'OpenRouter API authentication');
      apiKey = secretBreadcrumb.value;
    } catch (error) {
      throw new Error(`Failed to load API key secret: ${error.message}`);
    }
  } else {
    throw new Error('No API key configured. Please set apiKeySecretId in tool config.');
  }

  // Determine model (input overrides config)
  const model = input.model || config.defaultModel || 'google/gemini-2.0-flash-exp';
  const temperature = input.temperature ?? config.temperature ?? 0.7;
  const maxTokens = input.max_tokens || config.maxTokens || 4000;

  // Call OpenRouter API
  const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${apiKey}`,
      'Content-Type': 'application/json',
      'HTTP-Referer': 'http://localhost:3000',
      'X-Title': 'RCRT Agent System'
    },
    body: JSON.stringify({
      model,
      messages: input.messages,
      temperature,
      max_tokens: maxTokens
    })
  });

  if (!response.ok) {
    const errorText = await response.text();
    throw new Error(`OpenRouter API error ${response.status}: ${errorText}`);
  }

  const data = await response.json();

  // Calculate cost using models catalog
  let costEstimate = 0;
  try {
    const catalogResults = await context.api.searchBreadcrumbs({
      schema_name: 'openrouter.models.catalog.v1',
      tag: 'openrouter:models'
    });

    if (catalogResults.length > 0) {
      const catalog = catalogResults[0];
      const modelInfo = catalog.context.models?.find((m: any) => m.id === model);

      if (modelInfo?.pricing) {
        const promptCost = (data.usage?.prompt_tokens || 0) * parseFloat(modelInfo.pricing.prompt);
        const completionCost = (data.usage?.completion_tokens || 0) * parseFloat(modelInfo.pricing.completion);
        costEstimate = promptCost + completionCost;
      }
    }
  } catch (error) {
    console.warn('Failed to calculate cost from catalog:', error);
    // Fallback estimate
    const totalTokens = data.usage?.total_tokens || 0;
    costEstimate = (totalTokens / 1000) * 0.005; // $5 per 1M tokens default
  }

  return {
    content: data.choices[0].message.content,
    model: data.model,
    usage: data.usage || {
      prompt_tokens: 0,
      completion_tokens: 0,
      total_tokens: 0
    },
    cost_estimate: costEstimate
  };
}
