/**
 * Timer Tool - Self-Contained Deno Version
 * Waits for specified duration
 */

interface Input {
  seconds: number;
}

interface Output {
  waited: number;
  message: string;
}

interface Context {
  secrets: Record<string, string>;
  api: any;
  request: any;
}

export async function execute(input: Input, context: Context): Promise<Output> {
  if (typeof input.seconds !== 'number') {
    throw new Error('Missing or invalid field: seconds (must be a number)');
  }

  if (input.seconds < 0 || input.seconds > 60) {
    throw new Error('seconds must be between 0 and 60');
  }

  // Wait for the specified duration
  await new Promise(resolve => setTimeout(resolve, input.seconds * 1000));

  return {
    waited: input.seconds,
    message: `Waited ${input.seconds} seconds`
  };
}
